package javafds;

public class switchdemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int marks=2;
		switch(marks){
		case 1:
			System.out.println("main menu");
			break;
		case 2:
			System.out.println("2nd menu");
			break;
		default:
			System.out.println("no option");

	}

}
}
